import React, { useState, useEffect } from "react";
import Map, { NavigationControl, Marker, Popup } from "react-map-gl";
import Navbar from "./components/navbar.js";
import maplibregl from "maplibre-gl";
import "maplibre-gl/dist/maplibre-gl.css";
import "./App.css";
import defaultImage from "./macos-monterey-stock-green-dark-mode-layers-5k-6016x6016-5888.jpg"; // Import a default image for the popup

function App() {
  const [defaultPopup, setDefaultPopup] = useState(true); // State to control default popup visibility

  // Function to close the default popup after a certain delay (e.g., 5 seconds)
  useEffect(() => {
    const timer = setTimeout(() => {
      setDefaultPopup(false);
    }, 5000); // Adjust the delay as needed
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="App">
      <Navbar />
      <Map
        mapLib={maplibregl}
        initialViewState={{
          longitude: 77.48918219999996,
          latitude: 10.2381136,
          zoom: 14,
        }}
        style={{ width: "100%", height: " calc(100vh - 77px)" }}
        mapStyle="https://api.maptiler.com/maps/streets/style.json?key=KpFr32C3asC9l9tm2vNd"
      >
        <NavigationControl position="top-left" />
        {/* Custom marker with black color */}
        <Marker longitude={77.48918219999996} latitude={10.2381136}>
          <div
            className="marker"
            style={{
              backgroundColor: "black",
              width: "20px",
              height: "20px",
              borderRadius: "50%",
            }}
          />
        </Marker>
        {/* Default popup */}
        {defaultPopup && (
          <Popup
            longitude={77.48918219999996}
            latitude={10.2381136}
            onClose={() => setDefaultPopup(false)}
          >
            <div>
              <img
                src={defaultImage}
                alt="Default Place"
                style={{ width: "200px", height: "150px" }}
              />
              <p>Description of the default place.</p>
            </div>
          </Popup>
        )}
      </Map>
    </div>
  );
}

export default App;
